import { Collapsible, Button } from '@shopify/polaris';

function SectionHead(){
  return (
    <div style={{display:'flex',flexFlow:'row wrap',alignItems:'center',justifyContent:'space-between',padding:'1.5em 1em 0 1em'}}>
      <div style={{fontSize:'20px'}}>Custom Codes</div>
      <Button
        onClick={() => this.setState(()=>{ return {sectionActive:!this.state.sectionActive}})}
        ariaExpanded={this.state.sectionActive}
        ariaControls="basic-collapsible"
      >
        <span>
          <svg 
            viewBox="0 0 448 512" aria-hidden="true" 
            focusable="false" xmlns="http://www.w3.org/2000/svg" 
            role="img"
            style={{height:'1.5em',width:'1.5em'}}
          >
            <path 
              d="M240.971 130.524l194.343 194.343c9.373 9.373 9.373 24.569 0 33.941l-22.667 22.667c-9.357 9.357-24.522 9.375-33.901.04L224 227.495 69.255 381.516c-9.379 9.335-24.544 9.317-33.901-.04l-22.667-22.667c-9.373-9.373-9.373-24.569 0-33.941L207.03 130.525c9.372-9.373 24.568-9.373 33.941-.001z"
              fill='#000'></path>
          </svg>
        </span>
      </Button>
    </div>
  )
}
