import {Card, DatePicker, ChoiceList} from '@shopify/polaris';
import {useCallback, useState, useEffect}  from 'react';

function Choose(props){
  const [selected, setSelected] = useState(['always']);

  const handleChange = useCallback((value) => setSelected(value), []);

  const renderDatePicker = useCallback(
    (isSelected) =>
      isSelected && (
        <Schedule />
      )
  )

  return (
    <ChoiceList
      choices={[
        {label: 'Always display', value: 'always'},
        {label: 'Only display within the giving period of time', value: 'scheduled',renderChildren:renderDatePicker},
      ]}
      selected={selected}
      onChange={handleChange}
    />
  );
};

function Schedule(props){
  const [{ month, year }, setDate] = useState({
    month: 10,
    year: 2019
  });
  const [selectedDates, setSelectedDates] = useState({
    start: new Date(),
    end: new Date()
  });

  const handleMonthChange = useCallback((month, year) => {console.log('month changed',month,' ',year);setDate({ month, year })}, []);

  useEffect(()=>{},[selected])
  return (
    <DatePicker 
      month={month} 
      year={year} 
      onChange={setSelectedDates} 
      onMonthChange={handleMonthChange} 
      selected={selectedDates} 
      multiMonth={true} 
      allowRange={true} 
    />);
}

class DisplaySchedule extends React.Component{
  constructor(props){
    super(props);
    this.state={}
  }

  render(){
    return (
      <Card.Section>
        <div style={{marginBottom:'1em',fontSize:'1.1em'}}>Display schedule:</div>
        <Choose />
      </Card.Section>
    )
  }
}

export default DisplaySchedule;
