import { Card, Form, FormLayout } from '@shopify/polaris'

class CustomCodePage extends React.Component{
  constructor(props){
    super(props)
    this.state={
      customCode:''
    }
    this.handleCustomCode = this.handleCustomCode.bind(this);
  }

  handleCustomCode(val,type){
    const self = this;
    let scriptTxt, styleTxt;
    switch(type){
      case 'style':
        
    }
    if(this.state.timer){
      clearTimeout(this.state.timer);
    };
    this.setState(()=>{
      let timerN = setTimeout(() => {
        console.log('codes are sent');
        self.props.handleCustomCode(scriptTxt,styleTxt);
      }, 5000);
      return {timer:timerN}
    });
    
  }


  render(){
    return (
      <Card>
        <Form>
          <FormLayout>
            <div style={{fontSize:'18px',padding:'1.5em 0 0 1em'}}>Custom Codes</div>
            <Card sectioned>
              <div style={{fontSize:'16px',padding:'1.5em 0 0 1em'}}>Style</div>
              <div style={{fontSize:'14px',padding:'1.5em 0 0 1em'}}>&lt;style&gt;</div>
              <textarea rows="10" cols="80" placeholder="Please put your custom codes here..." 
                style={{marginLeft:'1em',border:'1px solid #bbb',borderRadius:'5px',width:'calc(100% - 2em)',marginBottom:'2em'}} 
                onChange={(evt) => {
                  evt.persist();
                  this.setState({timer:null})
                  this.handleCustomCode(evt.nativeEvent.target.value,'style')
                }}
              />
              <div style={{fontSize:'14px',padding:'1.5em 0 0 1em'}}>&lt;/style&gt;</div>
            </Card>
            <Card sectioned>
              <div style={{fontSize:'16px',padding:'1.5em 0 0 1em'}}>Script</div>
              <div style={{fontSize:'14px',padding:'1.5em 0 0 1em'}}>&lt;script&gt;</div>
              <textarea rows="10" cols="80" placeholder="Please put your custom codes here..." 
                style={{marginLeft:'1em',border:'1px solid #bbb',borderRadius:'5px',width:'calc(100% - 2em)',marginBottom:'2em'}} 
                onChange={(evt) => {
                  evt.persist();
                  this.setState({timer:null})
                  this.handleCustomCode(evt.nativeEvent.target.value,'script')
                }}
              />
              <div style={{fontSize:'14px',padding:'1.5em 0 0 1em'}}>&lt;/script&gt;</div>
            </Card>
          </FormLayout>
        </Form>
      </Card>
    )
  }
}

export default CustomCodePage;
