class PreviewBar extends React.Component{
  constructor(props){
    super(props);
    this.state = {animReq:null};
    this.htmlRef = React.createRef();
    this.injectHtml = this.injectHtml.bind(this);
  }

  requestAnimation(el){
    animReq = requestAnimationFrame(requestAnimation);
    let times = 3000*60;
    let dis = el.clientHeight/times; // moves up per second
    el.style.transform = `translateY(-${dis}px)`;

    let animKeyframe = `
     @keyframes infiniteSlide{
        from {
          transform: translateY(0)
        }
        to {
          transform: translateY(-50%)
        }
     }
    `
  }

  componentDidMount(){
    let injectedHtmlElCollection = this.htmlRef.current.children; // object
    for(let n=0;n<injectedHtmlElCollection.length;n++){
      let el = injectedHtmlElCollection[n];
      if(el.classList.contains('link-container')){
        el.setAttribute("style","height:22px;overflow:hidden;line-height:22px;");
        if(el.children[0].tagName==='UL'){
          let ul = el.children[0];
          let keyframeEl = document.createElement('style');
          keyframeEl.textContent = `
            @keyframes infiniteSlide{
                from {
                  margin-top:0
                }
                to {
                  margin-top: -22px
                }
            }
          `;
          ul.insertAdjacentElement('afterbegin',keyframeEl);
          ul.setAttribute("style","padding:0;margin:0;list-style:none;transition:all 2.5s;animation:infiniteSlide 2.5s;");
          ul.addEventListener('animationend',function(){
            let ulNode = ul.children[0].parentNode;
            let firstLi = ulNode.querySelector('li');
            console.log(firstLi);
            let clonedFirstLi = firstLi.cloneNode()
            // ul.insertAdjacentElement('beforeend',oriFirstChild);
            // ul.removeChild
          })
          // ul.style.transform=`translateY(-${ul.clientHeight / 2}px)`;
        }
      }
    }
  }

  componentDidUpdate(){
    console.log('component updated')
  }

  injectHtml(html){
    let contExp = /link\-container/;
    let havCont = contExp.test(html);
    if(havCont){

    }
    console.log('injected')
    return (
      <div dangerouslySetInnerHTML={{__html:html}} ref={this.htmlRef}></div>
    )
  }

  render(){
    const {bgColor, ftColor } = this.props.barConfig;
    return (
      <div style={{margin:'20px 0'}}>
        <div style={{padding:'8px 0',position:'relative',backgroundColor:bgColor,color:ftColor}}>
          { this.props.barConfig.goal?
            this.props.barConfig.inpTxt + '$' + this.props.barConfig.goal
            :
            this.props.barConfig.inpTxt
          }
          {/* #question: how to turn user input into raw html */}
          { this.props.barConfig.addedHtml?
              this.injectHtml(this.props.barConfig.addedHtml) 
              :
              null
          }
          <div style={{position:'absolute',top:'8px',right:'8px'}}>X</div>
        </div>
      </div>
    )
  }
}

export default PreviewBar;
