            let clonedFirstLi = firstLi.cloneNode(true);
            ulNode.insertAdjacentElement('beforeend',clonedFirstLi);
            ulNode.removeChild(firstLi);
            ul.style.animation = 'infiniteSlide 2.5s';
          })
        }
      }
    }
  }

  componentDidUpdate(){
    console.log('component updated')
  }

