class PreviewBar extends React.Component{
  constructor(props){
    super(props);
    this.state = {};
    this.htmlRef = 
    this.handleAddedHtml = this.handleAddedHtml.bind(this);
    this.injectHtml = this.injectHtml.bind(this);
  }

  handleAddedHtml(userInp){
    // if 'link-container' class exists, have a fixed height, hide overflows and alternate the inner content;
    let tagExp = /<\/(\w+)>/g;
    let tagNProps = /<(\w+['"=\s(\w)\:\;\{\}\/\.\-]*)>/g;
    let re, tagWhole ;
    let tagArr = [],tagWhArr=[];
    let classes = userInp.match(/class="([-\w\s]+)"/);
    let containerLinkStyle = {};
    while(re= tagExp.exec(userInp)){
      tagArr.push(re[1]);
      if(!re) break;
    }
    while(tagWhole = tagNProps.exec(userInp)){
      tagWhArr.push(tagWhole[1]);
      if(!tagWhole) break;
    }
    // to parse tagWhArr, make it an array of objects containing tag, prop and children
    tagWhArr.map((item,idx,arr)=>{
      let result={},propArr=[], props,propExp=/([-\w]+)="(['"\s\w\:\;\/\.\-]*)"/g,tagExp=/(\w+)[\s]+/;
      // if item.match(tagExp) is null, then tag is item, otherwise, tag is item.match(tagExp)[1]
      result.tag = !item.match(tagExp)? item : item.match(tagExp)[1];
      while(props = propExp.exec(item)){
        propArr.push({propertyName:props[1],propertyValue:props[2]});
        if(!props) break;
      };
      result.properties = propArr;
      arr[idx] = result;
    })
    if(classes){
      classes = classes[1].split(' ');
      if(classes.includes('container-link')){
        containerLinkStyle = {height:'22px',overflow:'hidden'}
      }
    }
    if(userInp) {
      let prop={};
      for(let n=0;n<tagArr.length;n++){
        switch(tagArr[n]){
          case 'a':
            prop['href']='';
            break;
        

        }
        return React.createElement(tagArr[n],prop,'any text')
      }
    } 
  }

  componentDidMount(){
    console.log('component mounted');

  }

  componentDidUpdate(){
    console.log('component updated')
  }

  injectHtml(html){
    let contExp = /link\-container/;
    let havCont = contExp.test(html);
    if(havCont){

    }
    console.log('injected')
    return (
      <div dangerouslySetInnerHTML={{__html:html}}></div>
    )
  }

  render(){
    const {bgColor, ftColor } = this.props.barConfig;
    return (
      <div style={{margin:'20px 0'}}>
        <div style={{padding:'8px 0',position:'relative',backgroundColor:bgColor,color:ftColor}}>
          { this.props.barConfig.goal?
            this.props.barConfig.inpTxt + '$' + this.props.barConfig.goal
            :
            this.props.barConfig.inpTxt
          }
          {/* #question: how to turn user input into raw html */}
          { this.props.barConfig.addedHtml?
              this.injectHtml(this.props.barConfig.addedHtml) 
              :
              null
          }
          <div style={{position:'absolute',top:'8px',right:'8px'}}>X</div>
        </div>
      </div>
    )
  }
}

export default PreviewBar;
