import { Card, Form, FormLayout } from '@shopify/polaris';
import PreviewBar from '../snippets/previewBar';

class PreviewPage extends React.Component{
  constructor(props){
    super(props);
    this.state={}
  }

  render(){
    console.log(this.props.contentConfig);
    const {barTxtConfig,barFrShGl} = this.props.contentConfig;
    return (
      <Card>
        <Form>
          <FormLayout>
            <PreviewBar barConfig={{bgColor:'#000',ftColor:'#fff',inpTxt:barTxtConfig.initialMsg1, goal:barFrShGl,addedHtml:'<div><a>Hello</a></div>'}} />
            <PreviewBar barConfig={{bgColor:'#000',ftColor:'#fff',inpTxt:barTxtConfig.prgMsg1, goal:barFrShGl-1,addedHtml:'<div class="container-link styudy" data-opt="thisO"><a href="https://">Hello</a></div>'}} />
            <PreviewBar barConfig={{bgColor:'#ab4e3d',ftColor:'#fff',inpTxt:barTxtConfig.achievedMsg, addedHtml:'<a>Hello</a>'}} />
          </FormLayout>
        </Form>
      </Card>
    )
  }
}

export default PreviewPage;
