import { Card, Stack, TextField, Button, Form, FormLayout, ColorPicker} from '@shopify/polaris';
import { useState, useCallback, useEffect } from 'react';
import ImageDropZone from '../snippets/imageDropZone';
import '../css/fonts.css';

function ColorPickers(props){
  const hexToHsl = (H) => {
    let r = 0, g = 0, b = 0;
    if (H.length == 4) {
      r = "0x" + H[1] + H[1];
      g = "0x" + H[2] + H[2];
      b = "0x" + H[3] + H[3];
    } else if (H.length == 7) {
      r = "0x" + H[1] + H[2];
      g = "0x" + H[3] + H[4];
      b = "0x" + H[5] + H[6];
    }
    // Then to HSL
    r /= 255;
    g /= 255;
    b /= 255;
    let cmin = Math.min(r,g,b),
        cmax = Math.max(r,g,b),
        delta = cmax - cmin,
        h = 0,
        s = 0,
        l = 0;

    if (delta == 0)
      h = 0;
    else if (cmax == r)
      h = ((g - b) / delta) % 6;
    else if (cmax == g)
      h = (b - r) / delta + 2;
    else
      h = (r - g) / delta + 4;

    h = Math.round(h * 60);

    if (h < 0)
      h += 360;

    l = (cmax + cmin) / 2;
    s = delta == 0 ? 0 : delta / (1 - Math.abs(2 * l - 1));
    s = +s;
    l = +l;

    return {"hue":h,"saturation":s,"brightness":l};
  };
  const [color, setColor] = useState({
    hue: 120,
    brightness: 1,
    saturation: 1,
  });
  let clFrmProp = props.thisColor;

  useEffect(()=>{
    let h = hexToHsl(clFrmProp).hue, s=hexToHsl(clFrmProp).saturation, l=hexToHsl(clFrmProp).brightness;
    debugger;
    console.log(h.toFixed(3),' ',s.toFixed(3),' ',l.toFixed(3));
    console.log(color.hue.toFixed(3),' ',color.saturation.toFixed(3),' ',color.brightness.toFixed(3));
    if((h!==undefined && h.toFixed(3)!==color.hue.toFixed(3)) || (s!==undefined && s.toFixed(3)!==color.saturation.toFixed(3)) || (l!==undefined && l.toFixed(3)!==color.brightness.toFixed(3))){
      props.handleColorChange(color);
      debugger;
    }
  },[color]);

  useEffect(()=>{
    // setColor(hexToHsl(clFrmProp));
    // debugger;
  },[clFrmProp]);

  return (
    <div style={{height:'80px',width:'80px',position:'absolute',top:'0',left:'0'}}>
      <ColorPicker onChange={setColor} color={color} />
    </div>
  );
}

function BackgroundSetting(props){
  const rgbToHex = (rgb) => {
    if(rgb.includes('#')) return rgb;
    let regExp = /\(\s*(\d+),\s*(\d+),\s*(\d+)\)/;
    let re = rgb.match(regExp);
    let r=parseInt(re[1]).toString(16),
        g=parseInt(re[2]).toString(16),
        b=parseInt(re[3]).toString(16);
    if (r.length == 1)
      r = "0" + r;
    if (g.length == 1)
      g = "0" + g;
    if (b.length == 1)
      b = "0" + b;
    return "#" + r + g + b;

  };
  const hslToRgb = (h,s,l) => { // s, l must be fractions of 100;
    let c = (1 - Math.abs(2 * l - 1)) * s,
      x = c * (1 - Math.abs((h / 60) % 2 - 1)),
      m = l - c/2,
      r = 0,
      g = 0,
      b = 0;
    if (0 <= h && h < 60) {
      r = c; g = x; b = 0;
    } else if (60 <= h && h < 120) {
      r = x; g = c; b = 0;
    } else if (120 <= h && h < 180) {
      r = 0; g = c; b = x;
    } else if (180 <= h && h < 240) {
      r = 0; g = x; b = c;
    } else if (240 <= h && h < 300) {
      r = x; g = 0; b = c;
    } else if (300 <= h && h < 360) {
      r = c; g = 0; b = x;
    }
    r = Math.round((r + m) * 255);
    g = Math.round((g + m) * 255);
    b = Math.round((b + m) * 255);
  
    return "rgb(" + r + "," + g + "," + b + ")";
  }
  const [showPicker,setShowPicker] = useState(false);
  const [bgColor, setBgColor] = useState('#000');
  const [bgOpacity, setBgOpacity] = useState('100');

  const [txtColor, setTxtColor] = useState('#b31219');
  const [specialColor, setSpecialColor] = useState('#fff');
  let bgColorFrmProps = props.colorConfig.bgColor, txtColorFrmProps = props.colorConfig.txtColor;
  let bgTimer,dur=2000;

  useEffect(()=>{
    props.handleStyleConfig({
      bgColor:bgColor,
      bgOpacity:bgOpacity,
      txtColor:txtColor,
      specialColor:specialColor
    },undefined);
  },[bgColor,bgOpacity,txtColor,specialColor]);

  useEffect(()=>{
    setBgColor(rgbToHex(bgColorFrmProps));
  },[bgColorFrmProps]);

  useEffect(()=>{
    setTxtColor(rgbToHex(txtColorFrmProps));
  },[txtColorFrmProps])

  const handleColorChange = (colorObj) => {
    // let cl = hslToRgb(colorObj.hue,colorObj.saturation,colorObj.brightness);
    // debugger;
    // setBgColor(rgbToHex(cl));
    // debugger;
  }

  return (
    <Card.Section>
      <Stack>
        <div>
          <h3>Background color:</h3>
          <div style={{height:'25px',width:'25px',borderWidth:'1px',borderStyle:'solid',borderColor:'#ddd',display:'inline-block',backgroundColor:bgColor,position:'relative'}} onClick={() => setShowPicker(!showPicker)}></div>
          { showPicker? 
            <ColorPickers handleColorChange={handleColorChange} />
            :
            null
          }
          <TextField
            value={bgColor}
            onChange={nw => {
              if (bgTimer) clearTimeout(bgTimer);
              debugger;
              bgTimer = setTimeout(() => {
                setBgColor(nw);
                debugger;
              }, dur); 
            }}
          />
        </div>
        <div>
          <h3>Background opacity:</h3>
          <div style={{display:'inline-block'}}>{bgOpacity}</div>
          <TextField 
            value={bgOpacity}
            onChange={nw => setBgOpacity(nw)}
            type="number"
            helpText="0 is transparent, 100 is opaque."
          />
        </div>
      </Stack>
      <Stack>
        <div>
          <h3>Text color:</h3>
          <div style={{height:'25px',width:'25px',borderWidth:'1px',borderStyle:'solid',borderColor:'#ddd',display:'inline-block',backgroundColor:txtColor}}></div>
          <TextField 
            value={txtColor}
            onChange={nw => setTxtColor(nw)}
          />
        </div>
        <div>
          <h3>Special text color:</h3>
          <div style={{height:'25px',width:'25px',borderWidth:'1px',borderStyle:'solid',borderColor:'#ddd',display:'inline-block',backgroundColor:specialColor}}></div>
          <TextField 
            value={specialColor}
            onChange={nw => setSpecialColor(nw)}
          />
        </div>
      </Stack>
    </Card.Section>
  )
}

function Fonts(props){
  const [fontSize,setFontSize] = useState(18);
  const [padding,setPadding] = useState(10);
  const [fontFam,setFontFam] = useState('sans-serif');

  const handleFontFam = useCallback(
    (val) => {
      setFontFam(val);
    },
    [fontFam]
  )

  const handleFontSize = useCallback(
    (val) => {
      setFontSize(val);
    },
    [fontSize]
  );

  const handlePadding = useCallback(
    (val) => {
      setPadding(val);
    },
    [padding]
  );

  useEffect(()=>{
    props.handleStyleConfig(undefined,{fontFamily:fontFam,fontSize:fontSize,barPadding:padding});
  },[fontFam,fontSize,padding])

  return (
    <Card.Section>
      <div>Font family:</div>
      <Button onClick={() => handleFontFam('Montserrat')}>Montserrat</Button>
      <Button onClick={() => handleFontFam('sans-serif')}>Sans serif</Button>
      <TextField 
        label="Font size:"
        suffix="px"
        type="number"
        value={fontSize}
        onChange={handleFontSize}
      />
      <TextField 
        label="Bar padding:"
        suffix="px"
        type="number"
        value={padding}
        onChange={handlePadding}
      />
    </Card.Section>
  )
}

class StyleConfigPage extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      colorConfig:{},
      fontConfig:{},
      bgImg:{}
    }
    this.handleStyleConfig = this.handleStyleConfig.bind(this);
    this.uploadBgImg = this.uploadBgImg.bind(this);
  }

  handleStyleConfig(colorCf,fontCf){
    this.setState((state)=>{
      if(colorCf===undefined){
        fontCf = {...state.fontConfig,...fontCf};
        return {colorConfig:state.colorConfig,fontConfig:fontCf}
      }else if(fontCf === undefined){
        colorCf = {...state.colorConfig,...colorCf};
        return {colorConfig:colorCf,fontConfig:state.fontConfig}
      }
    },function(){
      this.props.handleStyleConfig(this.state.colorConfig,this.state.fontConfig);
    })
  }

  uploadBgImg(bgFile){
    this.setState({bgImg:bgFile},function(){
      this.props.uploadBgImg(this.state.bgImg)
    })
  }

  render(){
    return (
      <Card>
        <Form>
          <FormLayout>
            <div style={{fontSize:'18px',padding:'1.5em 0 0 1em'}}>Style Configuration</div>
            <BackgroundSetting handleStyleConfig={this.handleStyleConfig} {...this.props.styleConfig}/>
            <ImageDropZone uploadBgImg={this.uploadBgImg}/>
            <Fonts  handleStyleConfig={this.handleStyleConfig}/>
          </FormLayout>
        </Form>
      </Card>
    )
  }
}

export default StyleConfigPage;
